    public class EmployeeVerifierProvider : IVerifierProvider {

      public  IEnumerable<Verifier> GetVerifiers(Object verifierProviderContext) {
        // Return a few verifiers.
        yield return GetFirstNameVerifier();
        yield return GetLastNameVerifier();
      }

      private Verifier GetLastNameVerifier() {
        return new StringLengthVerifier(typeof(Employee), "LastName", true, 3, 15);
      }

      private Verifier GetFirstNameVerifier() {
        return new StringLengthVerifier(typeof(Employee), Employee.PathFor(e => e.FirstName), false, null, 50);
      }
      
    }

          
    private DelegateVerifier SampleDelegateVerifier() {
      // A simple example adding a delegate verifier to ensure that Employee
      // BirthDate falls before HireDate.

      // Apply verifier only when have data for both properties.
      ApplicabilityConstraint<Employee> constraint = (emp, trigger, context) => {
        return emp.HireDate.HasValue && emp.BirthDate.HasValue ?
          VerifierApplicability.Yes : VerifierApplicability.InsufficientData;
      };

      // Define the verification performed - BirthDate must precede HireDate
      VerifierCondition<Employee> condition = (emp, trigger, context) => {
        return new VerifierResult(emp.BirthDate < emp.HireDate);
      };

      var errorMessage = "Must be born before hired";

      // Create the verifier.  Execute only after set and on instance.
      var v = new DelegateVerifier<Employee>(errorMessage, constraint, condition);
      v.VerifierOptions.ExecutionModes = VerifierExecutionModes.InstanceAndOnAfterSetTriggers;

      // Add triggers - setters for HireDate and BirthDate properties will trigger.
      v.AddTriggers(Employee.PropertyMetadata.HireDate.Name, Employee.PropertyMetadata.BirthDate.Name);

      return v;
    }

  
   private DelegateVerifier SampleDelegateVerifier() {
      // A simple example adding a delegate verifier for the Employee HireDate property.
      // This verifier could more easily be handled using a DateTimeRangeVerifier,
      // but is intended to show the parts to a DelegateVerifier.

      string errorMessage = "HireDate must be greater than MinValue";

      // Define the verification test. 
      VerifierCondition<Employee> condition = (emp, trigger, ctx) => {
        // Note when executed before the property is set that we must inspect the ProposedValue.
        DateTime? hireDate = (trigger.Timing == TriggerTiming.BeforeSet) ? (DateTime?) trigger.ProposedValue : emp.HireDate;
        return new VerifierResult(hireDate >= DateTime.Parse("1/1/2000"));
      };

      // Create the verifier.
      var v = new DelegateVerifier<Employee>(errorMessage, condition);

      // Add a trigger - here it's the HireDate property.
      v.AddTrigger(Employee.PropertyMetadata.HireDate.Name);

      return v;
    }

          
    private DelegateVerifier SampleDelegateVerifier() {
      // A simple example adding a delegate verifier to ensure that Employee
      // BirthDate falls before HireDate.

      // Apply verifier only when have data for both properties.
